import java.util.Scanner;

public class E51 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner in = new Scanner(System.in);
		
		System.out.print("Please enter an integer and I will tell you whether it is negative,zero, or positive : ");
		int numberInput = in.nextInt(); 
			
			if(numberInput > 0){
				System.out.println("The integer you have entered is positive.");}
			
			else if(numberInput == 0) {
				System.out.println("The integer you have entered is zero.");
			}
			
			else {
				System.out.println("The integer you have entered is negative");
			}
			
	
			
			
		
			
			
		
		
		
		
		
		
	}//class

}//main
